import { Fragment, useState, useEffect } from "react";

import Users from "./Users";

import classes from "./UserFinder.module.css";

import { Component } from "react";

const DUMMY_USERS = [
	{ id: "u1", name: "Max" },
	{ id: "u2", name: "Manuel" },
	{ id: "u3", name: "Julie" },
];

class UserFinder extends Component {
	constructor() {
		super();

		this.state = {
			filteredUsers: DUMMY_USERS,
			searchTerm: "",
		};
	}

	searchChangeHandler(event) {
		// setSearchTerm(event.target.value);
		this.setState({ searchTerm: event.target.value });
	}

	// side effects
	componentDidUpdate(prevProps, prevState) {
		if (prevState.filteredUsers !== this.state.filteredUsers) {
			let filteredUsers = DUMMY_USERS.filter((user) => user.name.includes(this.state.searchTerm));

			this.setState(prevState => {
        { filteredUsers: filteredUsers }
      });
		}
	}

	render() {
		// const [filteredUsers, setFilteredUsers] = useState(DUMMY_USERS);

		// const [searchTerm, setSearchTerm] = useState("");

		// useEffect(() => {
		// 	setFilteredUsers(DUMMY_USERS.filter((user) => user.name.includes(searchTerm)));
		// }, [searchTerm]);

		return (
			<Fragment>
				<div className={classes.finder}>
					<input type="search" onChange={this.searchChangeHandler.bind(this)} />
				</div>

				<Users users={this.state.filteredUsers} />
			</Fragment>
		);
	}
}

export default UserFinder;
