import { Component } from "react";

class MyErrorBoundary extends Component {
	constructor() {
		this.state = {
			hasError: false,
		};
	}

	componentDidCatch() {
		this.setState({ hasError: true });
	}

	render() {
		if (this.state.hasError) {
			return <> Something went wrong </>
		}
		return this.props.children;
	}
}

export default MyErrorBoundary;
