import React, { Component } from "react";

import UsersContext from "./users-context";

class UsersContextProvider extends Component {
	static contextType = UsersContext;
	constructor() {
		super();
		this.state = { hi: "true", DUMMY_USERS: [] };
	}
	render() {
		let DUMMY_USERS: [
			{ id: "u1", name: "Max" },
			{ id: "u2", name: "Manuel" },
			{ id: "u3", name: "Julie" },
		];

		const updateUsersHandler = () => {
			DUMMY_USERS = [
				{ id: "u1", name: "Max" },
				{ id: "u2", name: "Manuel" },
				{ id: "u3", name: "Julie" },
				{ id: "u4", name: "Jane" },
				{ id: "u5", name: "Aadi" },
				{ id: "u6", name: "Adi" },
				{ id: "u7", name: "Aaadi" },
				{ id: "u8", name: "Sikandar" },
				{ id: "u9", name: "Boss" },
			];
		};

		const usersContext = {
			users: DUMMY_USERS,
			updateUsers: updateUsersHandler,
		};

		return <UsersContext.Provider value={usersContext}> {this.props.children} </UsersContext.Provider>;
	}
}

export default UsersContextProvider;
